import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

const AllBlog = () => {
  const [blogs, setBlogs] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [searchQuery, setSearchQuery] = useState("");
  const blogsPerPage = 12;

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get("http://localhost:8080/getAllArticles");
        setBlogs(response.data);
      } catch (error) {
        console.error("Error fetching data: ", error);
      }
    };

    fetchData();
  }, []);

  const indexOfLastBlog = currentPage * blogsPerPage;
  const indexOfFirstBlog = indexOfLastBlog - blogsPerPage;
  const currentBlogs = blogs.slice(indexOfFirstBlog, indexOfLastBlog);

  const totalPages = Math.ceil(blogs.length / blogsPerPage);

  const paginate = (pageNumber) => setCurrentPage(pageNumber);

  const handleSearchChange = (event) => {
    setSearchQuery(event.target.value);
    setCurrentPage(1); // Reset to the first page when the search query changes
  };

  // Filter blogs based on the search query
  const filteredBlogs = blogs.filter((blog) =>
    blog.title.toLowerCase().includes(searchQuery.toLowerCase())
  );

  return (
    <div className=" bg-white">
      <div className="container mx-auto my-8 p-4 bg-white text-black">
      <div
  style={{
    background:
      "linear-gradient(rgba(0, 0, 0, 0.5), rgba(0, 0, 0, 0.5)), url(https://images.unsplash.com/photo-1684487747385-442d674962f2) no-repeat center",
    backgroundSize: "cover"
  }}
  className="py-52 px-1 md:px-8 text-center relative text-white font-bold text-2xl md:text-3xl overflow-auto"
>
  <h1 className="pb-4">Search for product</h1>
  <div className="w-11/12 md:w-3/4 lg:max-w-3xl m-auto">
    <div className="relative z-30 text-base text-black">
      <input
        type="text"
        defaultValue=""
        placeholder="Keyword"
        className="mt-2 shadow-md focus:outline-none rounded-2xl py-3 px-6 block w-full"
      />
      <div className="text-left absolute top-10 rounded-t-none rounded-b-2xl shadow bg-white divide-y w-full max-h-40 overflow-auto"></div>
    </div>
  </div>
</div>

        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
          {filteredBlogs.slice(indexOfFirstBlog, indexOfLastBlog).map((blog) => (
            <div key={blog.id} className="bg-white w-full my-4 rounded overflow-hidden shadow-lg">
              <Link to={`/blog-details/${blog.id}`}>
                <img className="w-full h-56 object-cover" src={blog.articles_image} alt={blog.title} />
              </Link>
              <div className="p-4">
                <Link to={`/blog-details/${blog.id}`}>
                  <div className="font-bold text-xl mb-2 text-black dark:text-white hover:text-red-700">
                    {blog.title}
                  </div>
                </Link>
                <p className="text-sm text-black dark:text-white">
                  {blog.content}
                </p>
              </div>
            </div>
          ))}
        </div>
        <div className="flex justify-center mt-4">
          {Array.from({ length: totalPages }).map((_, index) => (
            <button
              key={index + 1}
              onClick={() => paginate(index + 1)}
              className={`mx-2 p-2 bg-red-700 text-white hover:bg-black focus:outline-none rounded-full ${
                currentPage === index + 1 ? "bg-gray-800" : ""
              }`}
              style={{
                width: "40px",
                height: "40px",
                borderRadius: "50%",
              }}
            >
              {index + 1}
            </button>
          ))}
        </div>
      </div>
    </div>
  );
};

export default AllBlog;
