import React, { useState, useEffect } from 'react';
import AOS from 'aos';
import 'aos/dist/aos.css';

const Calculator = () => {
  const [gender, setGender] = useState('');
  const [height, setHeight] = useState('');
  const [weight, setWeight] = useState('');
  const [additionalInfo, setAdditionalInfo] = useState('');
  const [bmiResult, setBmiResult] = useState(null);
  const [status, setStatus] = useState('');
  const [error, setError] = useState('');
  const [showResult, setShowResult] = useState(false);

  useEffect(() => {
    AOS.init();
  }, []);

  const tableItems = [
    { name: '18-25', email: 'Normal Weight' },
    { name: '25-30', email: 'Overweight' },
    { name: '30-40', email: 'Obesity' },
    { name: '40-above', email: 'Morbid Obesity' },
  ];

  const calculateBMI = () => {
    const numericHeight = parseFloat(height);
    const numericWeight = parseFloat(weight);

    if (isNaN(numericHeight) || isNaN(numericWeight) || numericHeight <= 0 || numericWeight <= 0) {
      setError('Please enter valid positive values for height and weight.');
      return;
    }

    setError('');

    const bmi = (numericWeight / ((numericHeight / 100) ** 2)).toFixed(2);
    setBmiResult(bmi);

    const bmiStatus = getStatus(bmi);
    setStatus(bmiStatus);

    // Example additional information based on BMI
    const additionalInfo = getAdditionalInfo(bmi);
    setAdditionalInfo(additionalInfo);

    setShowResult(true);
  };

  const getStatus = (bmi) => {
    if (bmi < 18.5) return 'Underweight';
    else if (bmi >= 18.5 && bmi < 25) return 'Normal Weight';
    else if (bmi >= 25 && bmi < 30) return 'Overweight';
    else if (bmi >= 30 && bmi < 40) return 'Obesity';
    else return 'Morbid Obesity';
  };

  const getAdditionalInfo = (bmi) => {
    // Example: Provide additional information based on BMI
    if (bmi < 18.5) return 'Consider consulting with a nutritionist for a healthy weight gain plan.';
    else if (bmi >= 18.5 && bmi < 25) return 'You are within a healthy weight range.';
    else if (bmi >= 25 && bmi < 30) return 'Consider adopting a healthier lifestyle for weight management.';
    else if (bmi >= 30 && bmi < 40) return 'It is advisable to consult with a healthcare professional for weight management.';
    else return 'Consult with a healthcare professional for personalized advice on weight management.';
  };

  const resetCalculator = () => {
    setGender('');
    setHeight('');
    setWeight('');
    setAdditionalInfo('');
    setBmiResult(null);
    setStatus('');
    setError('');
    setShowResult(false);
  };

  return (
    <section data-aos="fade-up" className="calculator-section bg-black py-12 text-white">
      <div className="container mx-auto flex flex-wrap items-center justify-between">
        <div className="w-full md:w-1/2 md:pr-8 mb-8 md:mb-0">
          <h1 className="text-4xl font-bold mb-6 text-center md:text-left">BMI CALCULATOR</h1>
          <div className="flex flex-col md:flex-row md:space-x-4 mb-4">
            <div className="mb-4 md:w-1/3">
              <label className="block text-sm font-bold mb-2" htmlFor="height">
                Height (cm)
              </label>
              <input
                className="input-field w-full px-4 py-2 border rounded-md focus:outline-none focus:border-red-500"
                id="height"
                type="number"
                placeholder="e.g., 169"
                value={height}
                onChange={(e) => setHeight(e.target.value)}
              />
            </div>
            <div className="mb-4 md:w-1/3">
              <label className="block text-sm font-bold mb-2" htmlFor="weight">
                Weight (kg)
              </label>
              <input
                className="input-field w-full px-4 py-2 border rounded-md focus:outline-none focus:border-red-500"
                id="weight"
                type="number"
                placeholder="e.g., 68"
                value={weight}
                onChange={(e) => setWeight(e.target.value)}
              />
            </div>
            <div className="mb-4 md:w-1/3">
              <label className="block text-sm font-bold mb-2" htmlFor="additionalInfo">
                Additional Info
              </label>
              <input
                className="input-field w-full px-4 py-2 border rounded-md focus:outline-none focus:border-red-500"
                id="additionalInfo"
                type="text"
                placeholder="Additional Info"
                value={additionalInfo}
                readOnly
              />
            </div>
          </div>
          <div className="flex justify-center md:justify-start mb-6">
            <button
              className="p-3 bg-gradient-to-r rounded-xl bg-red-700 hover:bg-black transition-all duration-300 text-white mr-4"
              type="button"
              onClick={calculateBMI}
            >
              CALCULATE IT
            </button>
            <button
              className="p-3 bg-gradient-to-r rounded-xl bg-red-700 hover:bg-black transition-all duration-300 text-white"
              type="button"
              onClick={resetCalculator}
            >
              RESET IT
            </button>
          </div>
          {showResult && bmiResult !== null && (
            <div className="result-container text-center text-gray-800">
              <p className="mb-2">
                Your BMI is <span className="font-bold">{bmiResult}</span>.
              </p>
              <p>
                You are <span className="font-bold">{status}</span>.
              </p>
            </div>
          )}
        </div>
        <div className="w-full md:w-1/2 md:pl-8">
          <h3 className="text-2xl font-bold text-center md:text-left mb-4">BODY MASS INDEX</h3>
          <div className="shadow-sm border rounded-lg overflow-x-auto">
            <table className="w-full table-auto text-sm text-center bg-white">
              <thead className="bg-red-700 text-white font-medium border-b">
                <tr>
                  <th className="py-2 px-4">BMI</th>
                  <th className="py-2 px-4">CLASSIFICATION</th>
                  <th className="py-2 px-4">ADDITIONAL INFO</th>
                </tr>
              </thead>
              <tbody className="text-black divide-y">
                {tableItems.map((item, idx) => (
                  <tr key={idx} className="divide-x">
                    <td className="px-4 py-2">{item.name}</td>
                    <td className="px-4 py-2">{item.email}</td>
                    <td className="px-4 py-2">{getAdditionalInfo(item.name)}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Calculator;
