import React, { useEffect, useState } from "react";
import AOS from "aos";
import "aos/dist/aos.css";
import '../../App.css';

const Hero = () => {
  const [currentSlide, setCurrentSlide] = useState(1);

  const changeSlide = (slideNumber) => {
    setCurrentSlide(slideNumber);
  };

  useEffect(() => {
    AOS.init();
    const slideTimer = setTimeout(() => {
      const nextSlide = currentSlide === 4 ? 1 : currentSlide + 1;
      setCurrentSlide(nextSlide);
    }, 5000);
    return () => clearTimeout(slideTimer);
  }, [currentSlide]);

  return (
    <header className="bg-white dark:bg-gray-800 mt-24" data-aos="fade-up">
      <div className="w-full flex flex-col md:flex-row md:items-center">
        <div className="flex flex-col items-center w-full h-50" data-aos="fade-up">
          <div className="relative w-full md:order-2">
            {/* items */}
            {[1, 2, 3, 4].map((slideNumber) => (
              <div
                key={slideNumber}
                className={`w-full h-[550px] bg-cover bg-[50%] ${
                  currentSlide === slideNumber ? `block` : `hidden`
                } md:flex`}
                style={{
                  backgroundImage: `url(${
                    currentSlide === slideNumber
                      ? getSlideImage(slideNumber) // Function to get image URL based on slide number
                      : ""
                  })`,
                }}
                data-carousel-item=""
              >
                <div className="w-full h-full absolute top-0 bottom-0 right-0 left-0 bg-[#3f3f3f5c] flex flex-col justify-center items-center">
                  {/* ... (existing code) */}
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
      {/* Previous button */}
      <button
        type="button"
        className={`${
          currentSlide !== 1 ? `text-black` : `text-black`
        } absolute top-1/2 left-0 transform -translate-y-1/2 mx-6 p-2 md:p-4`}
        data-carousel-prev=""
        onClick={() =>
          changeSlide(currentSlide === 1 ? 4 : currentSlide - 1)
        }
      >
        <span className="flex items-center">
          <svg
            aria-hidden="true"
            className="w-8 h-8 md:w-16 md:h-16"
            fill="none"
            stroke="currentColor"
            viewBox="0 0 24 24"
            xmlns="http://www.w3.org/2000/svg"
          >
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth="2"
              d="M15 19l-7-7 7-7"
            ></path>
          </svg>
        </span>
      </button>

      {/* Next button */}
      <button
        type="button"
        className={`${
          currentSlide !== 1 ? `text-black` : `text-black`
        } absolute top-1/2 right-0 transform -translate-y-1/2 mx-6 p-2 md:p-4`}
        data-carousel-next=""
        onClick={() =>
          changeSlide(currentSlide === 4 ? 1 : currentSlide + 1)
        }
      >
        <span className="flex items-center">
          <svg
            aria-hidden="true"
            className="w-8 h-8 md:w-16 md:h-16"
            fill="none"
            stroke="currentColor"
            viewBox="0 0 24 24"
            xmlns="http://www.w3.org/2000/svg"
          >
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth="2"
              d="M9 5l7 7-7 7"
            ></path>
          </svg>
        </span>
      </button>
    </header>
  );
};

// Function to get image URL based on slide number
const getSlideImage = (slideNumber) => {
  switch (slideNumber) {
    case 1:
      return 'https://getwallpapers.com/wallpaper/full/d/1/9/200249.jpg';
    case 2:
      return 'https://wallpaperboat.com/wp-content/uploads/2019/06/workout-motivation-02.jpg';
    case 3:
      return 'https://e0.pxfuel.com/wallpapers/556/431/desktop-wallpaper-never-give-up-background-give-don-t-give-up.jpg';
    case 4:
      return 'https://wallpaper.dog/large/10697553.jpg';
    default:
      return '';
  }
};

export default Hero;
