import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

const BlogCard = ({ blog }) => {
  // ... (your existing BlogCard component remains unchanged)
};

const Blogs = () => {
  const [blogs, setBlogs] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const blogsPerPage = 3; // Display 3 cards per slide
  const totalBlogs = blogs.length;

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get("http://localhost:8080/getAllArticles");
        setBlogs(response.data);
      } catch (error) {
        console.error("Error fetching data: ", error);
      }
    };

    fetchData();
  }, []);

  const totalPages = Math.ceil(totalBlogs / blogsPerPage);
  const maxSlideIndex = totalPages - 1;
  const [currentSlide, setCurrentSlide] = useState(0);

  const nextSlide = () => {
    setCurrentSlide((prevSlide) => (prevSlide === maxSlideIndex ? 0 : prevSlide + 1));
  };

  const prevSlide = () => {
    setCurrentSlide((prevSlide) => (prevSlide === 0 ? maxSlideIndex : prevSlide - 1));
  };

  const startBlogIndex = currentSlide * blogsPerPage;
  const endBlogIndex = startBlogIndex + blogsPerPage;
  const currentBlogs = blogs.slice(startBlogIndex, endBlogIndex);

  return (
    <div className="flex flex-col items-center justify-center min-h-screen">
      <h1 className="text-black text-4xl mb-6 font-bold item-center justify-center text-center">
        Our Blogs
      </h1>
      <div className="relative w-full">
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-2 lg:grid-cols-3 gap-4 mx-4 my-4 w-full text-white">
          {currentBlogs.map((blog) => (
            <BlogCard key={blog.id} blog={blog} />
          ))}
        </div>
        <button
          className="absolute top-1/2 left-2 transform -translate-y-1/2 bg-red-700 text-white p-2 rounded-full"
          onClick={prevSlide}
        >
          Prev
        </button>
        <button
          className="absolute top-1/2 right-2 transform -translate-y-1/2 bg-red-700 text-white p-2 rounded-full"
          onClick={nextSlide}
        >
          Next
        </button>
      </div>
    </div>
  );
};

export default Blogs;
