import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

const BlogCard = ({ blog }) => {
  return (
    <div className="col-md-3 post-item">
      <figure>
        <img src={blog.articles_image} alt={blog.title} className="blogImg" />
      </figure>
      <div className="post-content pt-4">
        <span className="meta-date">March 10,2020</span>
        <h3 className="post-title">
          <Link to={`/blog-details/${blog.id}`} tabIndex="-1">
            {blog.title}
          </Link>
        </h3>
      </div>
    </div>
  );
};

const Blogs = () => {
  const [blogs, setBlogs] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const blogsPerPage = 4;
  const totalBlogs = blogs.length;

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get("http://localhost:8080/getAllArticles");
        setBlogs(response.data);
      } catch (error) {
        console.error("Error fetching data: ", error);
      }
    };

    fetchData();
  }, []);

  const totalPages = Math.ceil(totalBlogs / blogsPerPage);
  const maxSlideIndex = totalPages - 1;
  const [currentSlide, setCurrentSlide] = useState(0);

  const nextSlide = () => {
    setCurrentSlide((prevSlide) => (prevSlide === maxSlideIndex ? 0 : prevSlide + 1));
  };

  const prevSlide = () => {
    setCurrentSlide((prevSlide) => (prevSlide === 0 ? maxSlideIndex : prevSlide - 1));
  };

  useEffect(() => {
    const intervalId = setInterval(() => {
      nextSlide();
    }, 5000);

    return () => clearInterval(intervalId);
  }, [currentSlide]);

  const startBlogIndex = currentSlide * blogsPerPage;
  const endBlogIndex = startBlogIndex + blogsPerPage;
  const currentBlogs = blogs.slice(startBlogIndex, endBlogIndex);

  return (
    <section className="latest-blogs bg-black padding-medium light">
      <h2 className="section-title text-center mb-5">Blogs</h2>
      <div className="container">
        <div className="row">
          <div className="post-slider d-flex slick-initialized slick-slider">
            <button className="slick-prev slick-arrow" aria-label="Previous" type="button" style={{ display: "block" }} onClick={prevSlide}>
              Previous
            </button>
            <div className="col-md-3 post-item">
              {currentBlogs.map((blog) => (
                <BlogCard key={blog.id} blog={blog} />
              ))}
            </div>
            <button className="slick-next slick-arrow" aria-label="Next" type="button" style={{ display: "block" }} onClick={nextSlide}>
              Next
            </button>
          </div>
        </div>
        <div className="pix_btn light text-center mt-3">
          <a href="#" className="hvr-shutter-in-horizontal mt-3">
            View All
          </a>
        </div>
      </div>
    </section>
  );
};

export default Blogs;
