import React, { useState, useEffect } from 'react';
import AOS from 'aos';
import 'aos/dist/aos.css';

const Calculator = () => {
  const [gender, setGender] = useState('');
  const [height, setHeight] = useState('');
  const [weight, setWeight] = useState('');
  const [bmiResult, setBmiResult] = useState(null);
  const [status, setStatus] = useState('');
  const [error, setError] = useState('');
  const [showResult, setShowResult] = useState(false);

  useEffect(() => {
    AOS.init();
  }, []);

  const tableItems = [
    { name: '18-25', email: 'Normal Weight' },
    { name: '25-30', email: 'Overweight' },
    { name: '30-40', email: 'Obesity' },
    { name: '40-above', email: 'Morbid Obesity' },
  ];

  const calculateBMI = () => {
    const numericHeight = parseFloat(height);
    const numericWeight = parseFloat(weight);

    if (isNaN(numericHeight) || isNaN(numericWeight) || numericHeight <= 0 || numericWeight <= 0) {
      setError('Please enter valid positive values for height and weight.');
      return;
    }

    setError('');

    const bmi = (numericWeight / ((numericHeight / 100) ** 2)).toFixed(2);
    setBmiResult(bmi);

    const bmiStatus = getStatus(bmi);
    setStatus(bmiStatus);

    setShowResult(true);
  };

  const getStatus = (bmi) => {
    if (bmi < 18.5) return 'Underweight';
    else if (bmi >= 18.5 && bmi < 25) return 'Normal Weight';
    else if (bmi >= 25 && bmi < 30) return 'Overweight';
    else if (bmi >= 30 && bmi < 40) return 'Obesity';
    else return 'Morbid Obesity';
  };

  const resetCalculator = () => {
    setGender('');
    setHeight('');
    setWeight('');
    setBmiResult(null);
    setStatus('');
    setError('');
    setShowResult(false);
  };

  return (
    <section data-aos="fade-up" className="calculator-section flex items-center justify-center min-h-screen bg-[#f5f5f5]">
      <div className="container w-full mx-auto p-8 bg-white shadow-lg rounded-lg flex flex-col items-center justify-center">
        <div className="mb-8 flex items-center">
          <h1 className="text-3xl font-bold mb-6 text-center text-black">BMI CALCULATOR</h1>
          <h3 className="text-xl font-bold text-black mb-4 ml-4">BODY MASS INDEX</h3>
        </div>
        <div className="flex flex-wrap justify-between mb-4">
          {/* ... rest of the code ... */}
        </div>
        {/* ... rest of the code ... */}
      </div>
    </section>
  );
};

export default Calculator;
