import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

const AllBlog = () => {
  const [blogs, setBlogs] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const blogsPerPage = 12;

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get("http://localhost:8080/getAllArticles");
        setBlogs(response.data);
      } catch (error) {
        console.error("Error fetching data: ", error);
      }
    };

    fetchData();
  }, []);

  const indexOfLastBlog = currentPage * blogsPerPage;
  const indexOfFirstBlog = indexOfLastBlog - blogsPerPage;
  const currentBlogs = blogs.slice(indexOfFirstBlog, indexOfLastBlog);

  const totalPages = Math.ceil(blogs.length / blogsPerPage);

  const paginate = (pageNumber) => setCurrentPage(pageNumber);

  return (
    <div className=" mx-28 my-28">
    <div className="container mx-auto my-8 p-4 bg-black text-white">
      <h1 className="text-4xl font-bold text-center mb-8">
        All Blogs
      </h1>
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
        {currentBlogs.map((blog) => (
          <div key={blog.id} className="bg-white w-full my-4 rounded overflow-hidden shadow-lg">
            <Link to={`/blog-details/${blog.id}`}>
              <img className="w-full h-56 object-cover" src={blog.articles_image} alt={blog.title} />
            </Link>
            <div className="p-4">
              <Link to={`/blog-details/${blog.id}`}>
                <div className="font-bold text-xl mb-2 text-black dark:text-white hover:text-red-700">
                  {blog.title}
                </div>
              </Link>
              <p className="text-sm text-black dark:text-white">
                {blog.content}
              </p>
            </div>
          </div>
        ))}
      </div>
      <div className="flex justify-center mt-4">
        {Array.from({ length: totalPages }).map((_, index) => (
          <button
            key={index + 1}
            onClick={() => paginate(index + 1)}
            className={`mx-2 p-2 bg-red-700 text-white hover:bg-black focus:outline-none rounded-full ${
              currentPage === index + 1 ? "bg-gray-800" : ""
            }`}
            style={{
              width: "40px",
              height: "40px",
              borderRadius: "50%",
            }}
          >
            {index + 1}
          </button>
        ))}
      </div>
    </div>
    </div>
  );
};

export default AllBlog;
